def plano(x):    if x == "premium":        return 5    if x == "diamante":        return 4    if x == "ouro":        return 3    if x == "prata":        return 2    if x == "bronze":        return 1    if x == "resto":        return 0n = int(input())pacientes = []for i in range(n):    pacientes.append(input().split())    pacientes[i][2] = int(pacientes[i][2])pacientes = sorted(pacientes, key = lambda x: x[0])pacientes = sorted(pacientes, key = lambda x: x[2], reverse=True)pacientes = sorted(pacientes, key = lambda x: plano(x[1]), reverse=True)for i in range(n):    print(pacientes[i][0])