def calcula_maximo(x, y):    if x > y:        return x    return ynum_canos, tamanho_cano = [int(i) for i in input().split()]comprimento = [0 for _ in range(num_canos)]preco = [0 for _ in range(num_canos)]for i in range(num_canos):    comprimento[i], preco[i] = [int(j) for j in input().split()]tabela = [[0 for _ in range(tamanho_cano+1)] for _ in range(num_canos+1)]for i in range(1,num_canos+1):    for j in range(1,tamanho_cano+1):        if comprimento[i-1] > j:            tabela[i][j] = tabela[i-1][j]        else:            tabela[i][j] = calcula_maximo(                tabela[i-1][j],tabela[i-1][j - comprimento[i-1]] + preco[i-1])print(tabela[num_canos][tamanho_cano])