#Função imprime: recebe uma listadef imprime(medalhas, paises):    #Se só houver um país na lista, então imprime o país    if(len(medalhas) == 1):        print(paises[0], end=" ")        return    #O mínimo aponta para o último da lista    min = len(medalhas)-1    #Para cada medalha percorrida na ordem reversa    for i in reversed(range(len(medalhas))):        #Se encontrar um valor menor que o valor do mínimo        if medalhas[i] < medalhas[min]:            #Então tem-se um novo mínimo            min = i    #Ao final, armazena o país que tinha o mínimo    pais = paises[min]    #Remove o país da lista de medalhas e de índices de países    medalhas.pop(min)    paises.pop(min)    #Imprime o país com o mínimo de medalhas antes de imprimir este    imprime(medalhas, paises)    #Imprime o país atual    print(pais, end=" ")######################## Programa principal########################Lê N e M:n, m = map(int, input().split(" "))#Para cada país 1 <= i <= n, cria uma lista com n paísesmedalhas = [0]*n#Cria lista com o índices de 1 a n:paises = []for i in range(n):    paises.append(i+1)#Para cada modalidade 1 <= i <= m, ler o país que venceu cada medalhafor i in range(m):    o, p, b = map(int, input().split(" "))    #O número é convertido para a base (m+1) e somado ao total do país da    #seguinte maneira: medalha de ouro = (m+1)^2; prata = m+1; bronze = 1.    #Como cada país terá no máximo m medalas de um certo tipo, existirá uma    #objeção entre o número inteiro obtido desta soma e o termo (o,p,b)    medalhas[o-1] += (m+1)**2    medalhas[p-1] += (m+1)    medalhas[b-1] += 1#Para imprimir a colocação, chamamos a função recursiva:imprime(medalhas, paises)